.cp-loading-noscroll {
  overflow: hidden;
}
body.html {
  --LessLoader_require: "/customize/src/less2/include/font.less?ver=5.4.1";
  background-color: #EEEEEE;
  color: #424242;
  border: 0;
  padding: 0;
  margin: 0;
  font-size: 16px;
  font-family: 'Open Sans', 'Helvetica Neue', sans-serif;
}
body.html a {
  color: #0087FF;
}
body.html a:visited {
  color: #0079e6;
}
body.html a:hover {
  opacity: 1;
}
body.html #cp-main {
  display: flex;
  flex-flow: column;
  min-height: 100vh;
}
body.html #cp-main > .cp-container {
  flex: 1;
}
body.html .cp-container {
  font-size: 16px;
  padding-top: 32px;
  padding-bottom: 32px;
  min-height: 75vh;
  max-width: 1200px;
}
body.html .cp-container h1 {
  font-size: 3em;
  margin-bottom: 0.5em;
}
body.html .cp-container h2 {
  font-size: 1.8em;
  margin-top: 20px;
  margin-bottom: 0.5em;
}
body.html .cp-container h3 {
  font-size: 1.5em;
  margin-top: 1em;
  margin-bottom: 0.5em;
}
body.html .cp-container img {
  max-width: 100%;
  margin-top: 20px;
}
body.html .cp-container img.left {
  float: left;
}
body.html .cp-container .cp-page-title h1 {
  font-family: "IBM Plex Mono";
  color: #0087FF;
  margin: 50px auto;
}
body.html .cp-container p a {
  text-decoration: underline;
}
body.html .cp-container .row.cp-page-section {
  margin: 50px 0px;
}
body.html .cp-container .row.cp-page-section a {
  text-decoration: underline;
}
body.html .cp-container .row.cp-page-section .small-logo {
  max-width: 40%;
}
body.html .cp-container img.cp-shadow {
  box-shadow: 0px 0px 20px 0px rgba(0, 0, 0, 0.2);
}
body.html .cp-container img.cp-img-invert {
  filter: none;
}
body.html .cp-container button:not(.btn) {
  outline: none;
  background-color: #0087FF;
  color: #FFFFFF;
  border: none;
  padding: 10px 20px;
  margin-right: 10px;
  border-radius: 5px;
  cursor: pointer;
}
body.html .cp-container button:not(.btn):hover,
body.html .cp-container button:not(.btn):focus {
  background-color: #006ccc;
}
body.html .cp-container button.cp-secondary {
  background-color: #ffffff;
  color: #0087FF;
  border: 1px solid #0087FF;
}
body.html .cp-container .form-group {
  display: flex;
  flex-direction: column;
  align-items: center;
}
body.html .cp-container .form-group .checkbox-container {
  width: 100%;
  display: flex;
  align-items: center;
}
body.html .cp-container .form-group .checkbox-container label {
  margin: 0;
}
body.html .cp-container .form-group .checkbox-container input {
  margin-right: 0.5em;
}
body.html footer {
  background-color: rgba(153, 207, 255, 0.3);
  align-self: normal;
  box-shadow: inset 0px 7px 15px -7px rgba(0, 0, 0, 0.3);
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: row;
  flex-wrap: wrap;
}
body.html footer .cp-footer-left,
body.html footer .cp-footer-center,
body.html footer .cp-footer-right {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
}
body.html footer .cp-footer-left a,
body.html footer .cp-footer-center a,
body.html footer .cp-footer-right a {
  margin-right: 10px;
}
body.html footer .cp-logo-foot {
  display: flex;
  flex-flow: row;
  align-items: center;
  padding: 10px;
}
body.html footer .cp-logo-foot img {
  max-height: 40px;
  margin-right: 10px;
}
body.html footer .cp-logo-foot .logo-font {
  display: block;
  font-family: "IBM Plex Mono";
  font-size: 1.5rem;
  color: #424242;
  height: 40px;
}
body.html footer .cp-logo-btns {
  padding: 10px;
}
body.html footer .cp-logo-btns a {
  background-color: #0087FF;
  color: #FAFAFA;
  border-radius: 5px;
  padding: 0.5em 0.7em;
  white-space: nowrap;
  display: inline-block;
}
body.html footer .cp-logo-btns a i {
  margin-right: 5px;
}
body.html footer .cp-logo-btns a:hover {
  background-color: #FFF;
  color: #424242;
  text-decoration: none;
}
body.html footer .cp-footer-language {
  padding: 10px;
  margin-right: 10px;
}
body.html footer .cp-footer-language i {
  font-size: 1.3em;
  margin-right: 5px;
}
body.html footer .cp-footer-language select {
  min-width: 0;
  background-color: #FFF;
  color: #424242;
  border: 0px;
  border-radius: 5px;
  padding: 0.5em 0.7em;
}
body.html footer .cp-footer-version {
  padding: 10px;
  font-family: 'IBM Plex Mono';
}
body.html .navbar {
  width: 100%;
  color: #0087FF;
  justify-content: flex-end;
}
body.html .navbar .navbar-brand {
  display: block;
  height: 50px;
  width: 250px;
  font-family: "IBM Plex Mono";
  font-size: 1.8em;
  margin-right: 0;
}
body.html .navbar .navbar-brand img {
  max-width: 40px;
  margin-right: 10px;
}
@media (max-width: 326px) {
  body.html .navbar .navbar-brand {
    width: 180px;
  }
}
body.html .navbar a {
  white-space: nowrap;
  color: #0087FF;
  background-color: #FFF;
  border-radius: 5px;
}
body.html .navbar .nav-link {
  padding: 0.5em 0.7em;
  margin: 5px;
  color: #0087FF;
  display: inline-block;
}
body.html .navbar .nav-link img {
  max-height: 30px;
  margin-right: 10px;
}
body.html .navbar .nav-link.cp-back-home {
  margin-right: auto;
}
body.html .navbar .nav-link:hover {
  color: #424242;
}
body.html .navbar .nav-link i {
  margin-right: 5px;
}
body.html .navbar .cp-register-btn {
  background-color: #0087FF;
  color: #EEEEEE;
  display: inline-block;
}
body.html .navbar .cp-register-btn:hover {
  background-color: #FFF;
  color: #424242;
}
body.html .navbar button:focus {
  outline: none;
}
body.html .navbar .navbar-toggler {
  margin-top: 10px;
  color: #0087FF;
}

