            &.left, &:not(.right) {
                > * {

                }
            }
            // Top right placement
            &.right {
                > * {

                }
            }*/
}
.alertify-logs.left,
.alertify-logs:not(.right) {
  left: 16px;
}
.alertify-logs.left > *,
.alertify-logs:not(.right) > * {
  float: left;
  transform: translate3d(0, 0, 0);
  height: auto;
}
.alertify-logs.left > *.show,
.alertify-logs:not(.right) > *.show {
  left: 0;
