.alertify-logs.left > *,
.alertify-logs:not(.right) > *,
.alertify-logs.left > *.hide,
.alertify-logs:not(.right) > *.hide {
  left: -110%;
}
.alertify-logs.right {
  right: 16px;
}
.alertify-logs.right > * {
  float: right;
  transform: translate3d(0, 0, 0);
}
.alertify-logs.right > *.show {
  right: 0;
  opacity: 1;
}
.alertify-logs.right > *,
.alertify-logs.right > *.hide {
  right: -110%;
  opacity: 0;
}
.alertify-logs.top {
  top: 0;
  /* // Top left placement, use for transitions.
            &.left, &:not(.right) {
                > * {

                }
            }
            // Top right placement, use for transitions
            &.right {
                > * {

                }
            }*/
}
.alertify-logs > * {
  box-sizing: border-box;
  transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
  position: relative;
  clear: both;
  backface-visibility: hidden;
  perspective: 1000;
  max-height: 0;
  margin: 0;
  padding: 0;
  overflow: hidden;
  opacity: 0;
  pointer-events: none;
}
