  margin-left: 5px;
  z-index: 2;
  font-size: 13px;
}
.cp-limit-container .cp-limit-buttons {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  justify-content: space-evenly;
}
.cp-limit-container .cp-limit-buttons a {
  height: 25px;
  display: inline-flex;
  align-items: center;
  min-width: 150px;
  margin: 3px 5px;
  width: 50%;
  padding-top: 0;
  padding-bottom: 0;
  justify-content: center;
  flex: 1;
  border-radius: 5px;
}

.cp-app-noscroll {
  scrollbar-color: rgba(0, 0, 0, 0.2) transparent;
  height: 100%;
  width: 100%;
  padding: 0px;
  margin: 0px;
  overflow: hidden;
  box-sizing: border-box;
  position: relative;
  border: 0;
}
.cp-app-noscroll * {
  scrollbar-width: thin;
}
.cp-app-noscroll ::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}
.cp-app-noscroll ::-webkit-scrollbar-thumb {
  background-color: rgba(0, 0, 0, 0.2);
  width: 6px;
  height: 6px;
}
.cp-app-noscroll body {
  height: 100%;
  width: 100%;
  padding: 0px;
  margin: 0px;
  overflow: hidden;
  box-sizing: border-box;
  position: relative;
  border: 0;
}

#cp-loading {
  --LessLoader_require: "/customize/src/less2/include/password-input.less?ver=5.4.1";
  --LessLoader_require: "/customize/src/less2/include/forms.less?ver=5.4.1";
  visibility: visible;
  position: fixed;
  z-index: 10000000;
  top: 0px;
  bottom: 0px;
  left: 0px;
  right: 0px;
  background-color: #EEEEEE;
  color: #424242;
  font-size: 1.3em;
  line-height: 120%;
  opacity: 1;
  display: flex;
  flex-flow: column;
  justify-content: center;
  align-items: center;
  font: 20px 'Open Sans', 'Helvetica Neue', sans-serif !important;
}
@font-face {
  font-family: 'Open Sans';
  src: url('/components/open-sans-fontface/fonts/Regular/OpenSans-Regular.eot?ver=5.4.1');
  src: url('/components/open-sans-fontface/fonts/Regular/OpenSans-Regular.eot?&ver=5.4.1#iefix') format('embedded-opentype'), url('/components/open-sans-fontface/fonts/Regular/OpenSans-Regular.woff?ver=5.4.1') format('woff'), url('/components/open-sans-fontface/fonts/Regular/OpenSans-Regular.ttf?ver=5.4.1') format('truetype'), url('/components/open-sans-fontface/fonts/Regular/OpenSans-Regular.svg?ver=5.4.1#OpenSansRegular') format('svg');
  font-weight: normal;
  font-style: normal;
}
#cp-loading.cp-loading-transparent {
  background-color: rgba(238, 238, 238, 0.7);
}
#cp-loading.cp-loading-hidden {
  opacity: 0;
  visibility: hidden;
  transition: opacity 0.75s, visibility 0s 0.75s;
}
#cp-loading .cp-loading-logo {
  height: 300px;
  width: 300px;
  margin-top: 50px;
  flex: 0 1 auto;
  min-height: 0;
  text-align: center;
}
#cp-loading .cp-loading-logo img {
  max-width: 100%;
  max-height: 100%;
}
#cp-loading .cp-loading-container {
  width: 700px;
  max-width: 90vw;
  height: 236px;
  max-height: calc(100vh - 20px);
  margin: 50px;
  flex-shrink: 0;
  display: flex;
  flex-flow: column;
  align-items: center;
}
#cp-loading .cp-loading-cryptofist {
  margin-left: auto;
  margin-right: auto;
  max-width: 90vw;
  max-height: 300px;
  width: auto;
  height: auto;
  margin-bottom: 2em;
}
@media screen and (max-width: 500px) {
  #cp-loading {
    font-size: 16px !important;
  }
  #cp-loading .cp-loading-container {
    height: 206px;
  }
}
@media screen and (max-height: 700px) {
  #cp-loading {
    font-size: 16px !important;
  }
  #cp-loading .cp-loading-container {
    height: 206px;
  }
}
@media screen and (max-height: 500px) {
  #cp-loading .cp-loading-logo {
    display: none;
  }
}
#cp-loading #cp-loading-message {
  background: #FFF;
  padding: 20px;
  width: 100%;
  border-radius: 5px;
  color: #424242;
  text-align: left;
  display: none;
}
#cp-loading #cp-loading-message a {
  color: #0087FF;
}
#cp-loading #cp-loading-password-prompt p.cp-password-error {
  color: #424242;
  background: rgba(255, 17, 0, 0.5);
  padding: 5px;
  margin-bottom: 15px;
}
#cp-loading #cp-loading-password-prompt .cp-password-form {
  display: flex;
  flex-wrap: wrap;
  width: 100%;
}
#cp-loading #cp-loading-password-prompt .cp-password-form .cp-password-container {
  flex-shrink: 1;
  min-width: 0;
}
#cp-loading #cp-loading-password-prompt .cp-password-form .cp-password-reveal {
  padding: 0px 24px;
}
#cp-loading #cp-loading-password-prompt .cp-password-input {
  font-size: 16px;
}
#cp-loading #cp-loading-password-prompt::-webkit-input-placeholder {
  /* WebKit, Blink, Edge */
  color: rgba(66, 66, 66, 0.75);
  font-weight: normal;
}
#cp-loading #cp-loading-password-prompt::placeholder {
  /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: rgba(66, 66, 66, 0.75);
  opacity: 1;
  /* Firefox */
  font-weight: normal;
}
#cp-loading #cp-loading-password-prompt:-ms-input-placeholder {
  /* Internet Explorer 10-11 */
  color: rgba(66, 66, 66, 0.75);
  font-weight: normal;
}
#cp-loading #cp-loading-password-prompt::-ms-input-placeholder {
  /* Microsoft Edge */
  color: rgba(66, 66, 66, 0.75);
  font-weight: normal;
}
#cp-loading p.cp-password-info {
  text-align: left;
  margin-bottom: 15px;
}
#cp-loading .cp-loading-spinner-container {
  position: relative;
  height: 80px;
  margin-bottom: 50px;
}
#cp-loading .cp-loading-progress {
  width: 100%;
  text-align: center;
}
#cp-loading .cp-loading-progress p {
  margin: 5px;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}
#cp-loading .cp-loading-progress .cp-loading-progress-list {
  text-align: left;
  display: inline-block;
  margin-bottom: 50px;
  max-width: 100%;
}
#cp-loading .cp-loading-progress .cp-loading-progress-list ul {
  list-style: none;
  padding-left: 0;
  margin: 0;
}
#cp-loading .cp-loading-progress .cp-loading-progress-list li {
  padding: 0px 5px;
  user-select: none;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -khtml-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
#cp-loading .cp-loading-progress .cp-loading-progress-list li i {
  width: 22px;
}
#cp-loading .cp-loading-progress .cp-loading-progress-list li span {
  margin-left: 10px;
}
#cp-loading .cp-loading-progress .cp-loading-progress-list li span.percent {
  position: absolute;
}
#cp-loading .cp-loading-progress .cp-loading-progress-bar {
  height: 24px;
  background: #FFF;
  border: 1px solid #0087FF;
  border-radius: 5px;
}
#cp-loading .cp-loading-progress .cp-loading-progress-bar-value {
  height: 100%;
  background: #0087FF;
}
@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(1800deg);
  }
}
#cp-loading .cp-spinner {
  display: inline-block;
  box-sizing: border-box;
  width: 80px;
  height: 80px;
  border: 11px solid #9E9E9E;
  border-radius: 50%;
  border-top-color: transparent;
  animation: spin infinite 3s;
  animation-timing-function: cubic-bezier(0.6, 0.15, 0.4, 0.85);
}
